#ifdef CLEAR_FLAGS
void msc_clear_flags (void) {
	gpit = MAX_FLAGS; while (gpit --) flags [gpit] = 0;
}
#endif

unsigned char read_byte (void) {
	return *script ++;
}

unsigned char read_vbyte (void) {
	sc_c = *script ++;
	if (sc_c & 128) return flags [sc_c & 127];
	return sc_c;
}

void readxy (void) {
	sc_x = read_byte ();
	sc_y = read_byte ();
}

void readvxy (void) {
	sc_x = read_vbyte ();
	sc_y = read_vbyte ();
}

void run_script (unsigned char whichs) {
	commands_executed = 0;
    script_result = 0;
	script = script_pool [whichs];
    if (script == 0) return;

	// todo : update selected item flag

	while ((sc_c = read_byte ()) != 0xff) {
		next_script = script + sc_c;
		sc_terminado = sc_continuar = 0;
		while (!sc_terminado) {
			switch (read_byte ()) {
				case 0x10:
					// IF FLAG sc_x = sc_n
					// Opcode: 10 sc_x sc_n
					readvxy ();
					sc_terminado = (flags [sc_x] != sc_y);
					break;
				case 0x13:
					// IF FLAG sc_x <> sc_n
					// Opcode: 13 sc_x sc_n
					readvxy ();
					sc_terminado = (flags [sc_x] == sc_y);
					break;
				case 0x20:
					// IF PLAYER_TOUCHES x, y
					// Opcode: 20 sc_x sc_y
					readvxy ();
					sc_x <<= 4; sc_y <<= 4;
					sc_terminado = (!(prx + 7 >= sc_x && prx <= sc_x + 15 && pry + 15 >= sc_y && pry <= sc_y + 15));
					break;
				case 0x21:
					// IF PLAYER_IN_X x1, x2
					// Opcode: 21 x1 x2
					readxy ();
					sc_terminado = (!(prx >= sc_x && prx <= sc_y));
					break;
				case 0x22:
					// IF PLAYER_IN_Y y1, y2
					// Opcode: 22 y1 y2
					readxy ();
					sc_terminado = (!(pry >= sc_x && pry <= sc_y));
					break;
				case 0xff:
					// then
					// opcode: ff
					sc_terminado = sc_continuar = 1;
					break;
			}
		}

		if (sc_continuar) {
			sc_terminado = 0;
			commands_executed = 1;
			while (!sc_terminado) {
				switch (sc_n = read_byte ()) {
					case 0x01:
						// SET FLAG sc_x = sc_n
						// Opcode: 01 sc_x sc_n
						readvxy ();
						flags [sc_x] = sc_y;
						break;
					case 0x15:
						// FLIPFLOP sc_x
						// Opcode: 15 sc_x
						sc_x = read_vbyte ();
						flags [sc_x] = 1 - flags [sc_x];
						break;
					case 0x17:
						// DECRANGE sc_n, sc_x, sc_y
						sc_n = read_vbyte ();
						readvxy ();
						flags [sc_n] = flags [sc_n] > sc_x ? flags [sc_n] - 1 : sc_y;
						break;
					case 0x18:
						// INCRANGE sc_n, sc_x, sc_y
						sc_n = read_vbyte ();
						readvxy ();
						flags [sc_n] = flags [sc_n] < sc_y ? flags [sc_n] + 1 : sc_x;
						break;
					case 0x19:
					case 0x1a:
						flags [read_vbyte ()] = sc_n & 1;
						break;
					case 0x20:
						// SET TILE (sc_x, sc_y) = sc_n
						// Opcode: 20 sc_x sc_y sc_n
						readvxy ();
						ul = update_list;
						set_map_tile (sc_x, sc_y, read_vbyte ());
						close_update_cycle ();
						break;
					case 0x51:
						// SET_FIRE_ZONE x1, y1, x2, y2
						// Opcode: 51 x1 y1 x2 y2
						fzx1 = read_byte ();
						fzy1 = read_byte ();
						fzx2 = read_byte ();
						fzy2 = read_byte ();
						f_zone_ac = 1;
						break;
					case 0x86:
						// ADD_CONTAINER f, x, y
						sc_n = read_vbyte (); readvxy ();
						containers_add ();
						break;
					case 0xE0:
						// SOUND sc_n
						// Opcode: E0 sc_n
						sfx_play (read_vbyte (), 1);
						break;
					case 0xE2:
						// RECHARGE
						plife = LIFE_INI;
						break;
					case 0xE4:
						// EXTERN sc_n
						// Opcode: 0xE4 sc_n
						sc_n = read_byte ();
						do_extern_action ();
						break;
					case 0xE5:
						// PAUSE sc_n
						// Opcode: 0xE5 sc_n
                        delay (read_vbyte ());
						break;
					case 0xF1:
						// WIN
						// Opcode: 0xF1
						script_result = 1;
						return;
					case 0xF2:
						// BREAK
						// Opcode: 0xF2
						return;
					case 0xF4:
						// DECORATIONS
						// Opcode: 0xF4 XY T XY T XY T... 0xff
						while (0xff != (sc_x = read_byte ())) {
							ul = update_list;
							set_map_tile (sc_x >> 4, sc_x & 15, read_byte ());
							close_update_cycle ();
						}
						break;
					case 0xff:
						sc_terminado = 1;
						break;
				}
			}
		}
		script = next_script;
	}
}
